# coding: utf-8# Выполнил Корнаухов Леонид, (4 курс ИС) с помощью # неизвестного программиста из интернета.# # Программа строит магические квадраты нечетного порядка# 28.02.2025 13:30 - 15:30# # Особенность реализации: отсутствие форматного вывода...# -------------------------------------------------------from os import systemdef building(shape):    if shape % 2 == 0:        raise exception('Вы пытаетесь построить квадрат четного порядка')    if shape >= 11:        temp = input('Магический квадрат введеной размерности не влезет при выводе, желаете продолжить?(Y/N)')        if temp in ['N','n']:            return 'end'        elif temp in ['Y','y']:            pass        magicsquare = [[ 0 for x in range(shape)] for y in range(shape)]             # initialize position of 1    i = shape // 2    j = shape - 1    # Fill the magic square    # by placing values    num = 1    while num <= (shape * shape):        if i == -1 and j == shape:  # 3rd condition                j = shape - 2                i = 0        else:                # next number goes out of                # right side of square                if j == shape:                    j = 0                # next number goes                # out of upper side                if i < 0:                    i = shape - 1        if magicsquare[int(i)][int(j)]:  # 2nd condition                j = j - 2                i = i + 1                continue        else:            magicsquare[int(i)][int(j)] = num            num = num + 1        j = j + 1        i = i - 1  # 1st condition        return magicsquaredef control(res,shape):    i = len(res)    j = len(res[0])    row_sum = [sum(row) for row in res]    column_sum = [sum(res[n][m] for n in range(i)) for m in range(j)]    diag_sum = sum(res[n][n] for n in range(min(i,j)))    diag_sum2 = sum(res[n][j-1-n] for n in range(min(i,j)))    all_sum = row_sum + column_sum + [diag_sum,diag_sum2]    print(all_sum)    if all(x == all_sum[0] for x in all_sum):        return 'Все суммы равны'    else:        return 'Суммы не равны'    shape = int(input('Введите размер магического квадрата: '))res = building(shape)print('\n'.join('\t'.join(map(str,row)) for row in res))print(control(res,shape))system('pause')